function criaAnime(image,titulo, text){
  return {
    image,
    titulo,
    text,
    lista: document.querySelector('section#animes'),
    
    criaElemento(tipo,clas){
      const elemento = document.createElement(tipo)
      elemento.classList.add(clas)
      return elemento
    },
    addBox(box, elemento){
      box.appendChild(elemento)
    },
    cria(){
      const box = this.criaElemento('div', 'box')
      
      const img = this.criaElemento('img')
      img.src = this.image
      this.addBox(box, img)
      
      const titulo = this.criaElemento('h2')
      titulo.innerText = this.titulo
      this.addBox(box, titulo)

      const textWraper = this.criaElemento('div', 'text-wraper')
      textWraper.innerText = this.text
      this.addBox(box, textWraper)

      const a = this.criaElemento('a')
      a.innerText = 'assista'
      this.addBox(box ,a)

      this.addBox(this.lista, box)
    }

  }
}
const lorem = 'Lorem ipsum dolor, sit amet consectetur adipisicing elit. Corporis aliquid amet sed quisquam. Voluptatem ut aliquam dolores itaque eius vel, doloribus ipsum mollitia facilis doloremque quis distinctio. Eaque, ratione corporis.'
const linkSao = 'data:image/jpeg;base64,'
const linkAkame = 'data:image/jpeg;base64,'
const linkRomeoXjuliet = 'data:image/jpeg;base64,'

const naruto = criaAnime('https://nerdhits.com.br/wp-content/uploads/2020/10/naruto-uzumaki-perguntas.jpg', 'naruto', lorem)
const hyouka = criaAnime('https://i.ytimg.com/vi/uf3a63J2A9I/maxresdefault.jpg', 'hyouka', lorem)
const sao = criaAnime(linkSao, 'sword art online', lorem)
const akame = criaAnime(linkAkame, 'akame ga kill', lorem)
const steins = criaAnime('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQGoblCJC6Emj1gohYFI8GHzWWbEKnOciHkgA&usqp=CAU', 'steins gate', lorem)
const darling = criaAnime("data:image/jpeg;base64,", 'darling in the franxx', lorem)
const romeoXjuliet = criaAnime(linkRomeoXjuliet, 'romeo x juliet', lorem)

const animes = [naruto, hyouka, sao, akame, steins, darling, romeoXjuliet]

animes.forEach(valor => valor.cria())